class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        auto opCount = 0ll;
        auto preDiff = 0;
        for (auto i = 0; i < nums.size(); ++i) {
            auto diff = target[i] - nums[i];
            if (diff > 0 && preDiff > 0) {
                opCount += max(0, diff - preDiff);
            } else if (diff < 0 && preDiff < 0) {
                opCount += max(0, preDiff - diff);
            } else {
                opCount += abs(diff);
            }
            preDiff = diff;
        }
        return opCount;
    }
};
