import numpy as np
class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diff = [tar - num for tar,num in zip(target,nums)]
        d_diff = np.diff([0]+diff)
        offset = sum(d_diff)
        pos = sum(d for d in d_diff if d>0)
        neg = sum(-d for d in d_diff if d<0)
        if offset>0:
            return offset + neg
        else:
            return -offset + pos
