class Solution {
public:
    using ll = long long;
    vector<ll> l, r;
    ll n;

    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        n = nums.size();
        l.resize(n), r.resize(n);
        for(int i = 0; i < n; i++){
            int cur = nums[i] - target[i];
            if(cur < 0) l[i] = -cur;
            else r[i] = cur;
        }
        ll ans = 0;
        ans = r[0] + l[0];
        for(int i = 1; i < n; i++){
            ans += max(0ll, l[i] - l[i-1]);
            ans += max(0ll, r[i] - r[i-1]);
        }
        return ans;
    }
};
