func minimumOperations(nums []int, target []int) int64 {
    diff := make([]int, len(nums))
    for i := 0; i < len(nums); i++ {
        diff[i] = target[i] - nums[i]
    }
    tot := int64(0)
    runp := 0
    runn := 0
    for i := 0; i < len(diff); i++ {
        if diff[i] < 0 {
            if runp > 0 {
                runp = 0
            }
            if -diff[i] > runn {
                tot += int64(-diff[i] - runn)
            } 
            runn = -diff[i]
        }
        if diff[i] > 0 {
            if runn > 0 {
                runn = 0
            }
            if diff[i] > runp {
                tot += int64(diff[i] - runp)
            }
            runp = diff[i]
        }
        if diff[i] == 0 {
            runp = 0
            runn = 0
        }
    }
    return tot
}
