class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> arr(n);
        for(int i=0;i<n;i++){
            arr[i] = target[i] - nums[i];
        }
        long long ans=abs(arr[0]);
        for(int i=1 ;i<n;i++){
            if(arr[i] < 0 ){
                if(arr[i-1] >= 0){
                    ans+= abs(arr[i]);
                }
                 else if(arr[i-1] < 0 && arr[i] < arr[i-1]){
                    ans += abs(arr[i])-abs(arr[i-1]);
                }
            }
            else if(arr[i] > 0){
                if(arr[i-1] <=0)
                    ans += arr[i];
                else if(arr[i-1] > 0 && arr[i] > arr[i-1]){
                    ans += arr[i] - arr[i-1];
                }
            }
        }
        return ans;
    }
};
