class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        d, n = [], len(nums)
        for i in range(n):
            d.append(target[i] - nums[i])
        # 将数组所有元素变为 0 的最小操作数
        last_op = 0
        cnt = 0
        for i in range(n):
            if last_op * d[i] <= 0:
                cnt += abs(d[i])
            elif abs(last_op) < abs(d[i]):
                cnt += abs(d[i]) - abs(last_op)
            elif abs(d[i]) < abs(last_op):
                cnt += 0
            last_op = d[i]
        return cnt
