func minimumOperations(nums []int, target []int) int64 {
    n := len(nums)
    arr := make([][2]int, n, n)
    var res int64
    diff := target[0] - nums[0]
    res += int64(abs(diff))
    if diff > 0 {
        arr[0][0] = diff
    } else {
        arr[0][1] = -diff
    }
    for i := 1; i < n; i += 1 {
        diff := target[i] - nums[i]
        if diff > 0 {
            if diff > arr[i - 1][0] {
                res += int64(diff - arr[i - 1][0])
                arr[i][0] = diff
            } else {
                arr[i][0] = diff
            }
        } else if diff < 0 {
            if -diff > arr[i - 1][1] {
                res += int64(-diff - arr[i - 1][1])
                arr[i][1] = -diff
            } else {
                arr[i][1] = -diff
            }
        }
        //fmt.Println(arr[i], res)
    }
    return res
}

func abs(num int) int {
    if num > 0 {
        return num
    }
    return -num
}

func getDir(num int) int {
    if num > 0 {
        return 1
    }
    if num == 0 {
        return 0
    }
    return -1
}
