class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long result = 0;
        for (int i = 0; i < nums.size(); ++i) {
            if (nums[i] > target[i]) {
                int j = i;
                int decValue = nums[i] - target[i];
                while (j < nums.size()) {
                    if (nums[j] - target[j] <= 0) {
                        break;
                    }
                    decValue = min(decValue, nums[j] - target[j]);
                    ++j;
                }
                for (int x = i; x < j; ++x) {
                    nums[x] -= decValue;
                }
                result += decValue;
                --i;
            } else if (nums[i] < target[i]) {
                int j = i;
                int incValue = target[i] - nums[i];
                while (j < nums.size()) {
                    if (target[j] - nums[j] <= 0) {
                        break;
                    }
                    incValue = min(incValue, target[j] - nums[j]);
                    ++j;
                }
                for (int x = i; x < j; ++x) {
                    nums[x] += incValue;
                }
                result += incValue;
                --i;
            } 
        }
        return result;
    }
};
