#define ll long long
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        ll ans=0;
        int n = target.size();
        int i=0;
        int prev=0;
        while(i<n){
            prev=0;
            while(i<n && nums[i]<=target[i]){
                ans+=max((abs(nums[i]-target[i])-prev),0);
                prev=abs(nums[i]-target[i]);
                i++;
            }
            prev=0;
            while(i<n && nums[i]>target[i]){
                ans+=max((abs(nums[i]-target[i])-prev),0);
                prev=abs(nums[i]-target[i]);
                i++;
            }
        }
        return ans;
    }
};
