func minimumOperations(nums []int, target []int) int64 {
    n := len(nums)
    diff := make([]int, n)
    for i := 0; i < n; i++ {
        diff[i] = target[i] - nums[i]
    }
    // fmt.Println(diff)
    temp := int64(diff[0])
    if diff[0] < 0 {
        temp = -int64(diff[0])
    }
    // fmt.Print(temp, " ")
    for i := 1; i < n; i++ {
        if diff[i] == 0 {
            fmt.Print(temp, " ")
            continue
        }
        if diff[i] > 0 {
            temp = temp + int64(diff[i])
            if diff[i-1] > 0 {
                if diff[i-1] > diff[i] {
                    temp -= int64(diff[i])
                } else {
                    temp -= int64(diff[i-1])
                }
            }
        } else {
            temp = temp - int64(diff[i])
            if diff[i-1] < 0 {
                if diff[i-1] < diff[i] {
                    temp += int64(diff[i])
                } else {
                    temp += int64(diff[i-1])
                }
            }
        }
        // fmt.Print(temp, " ")
    }
    return temp
}
