class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        m = len(nums)
        op_count = 0
        carry = 0
        for i in range(m):
            diff = nums[i] - target[i]

            if diff == 0:
                carry = 0
            elif carry >= 0 and diff > 0:
                if carry >= diff:
                    carry = diff
                else:
                    op_count += diff - carry
                    carry = diff
            elif carry <= 0 and diff < 0:
                if carry <= diff:
                    carry = diff
                else:
                    op_count += carry - diff
                    carry = diff
            else:
                op_count += abs(diff)
                carry = diff

        return op_count
