class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diff = [(target[i] - nums[i]) for i in range(len(target))]

        print(diff)

        ct = 0
        res = 0

        for d in diff:
            if res != d:
                if res > d > 0 or res < d < 0:
                    res = d
                ct += min(abs(d), abs(d - res))
                res = d

            print(d, ct)
        return ct
