class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        for(int i = 0; i < nums.size(); i++) nums[i] -= target[i];
        long long result = abs(nums[0]);
        for(int i = 1; i < nums.size(); i++) {
            if(nums[i] > 0) {
                if(nums[i-1] > 0) result += max(0, nums[i] - nums[i-1]);
                else result += nums[i];
            } else {
                if(nums[i-1] < 0) result += max(0, -nums[i] - -nums[i-1]);
                else result += -nums[i];
            }
        }
        return result;
    }
};
