class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        long long res = 0, pre = 0;

        for (int i = 0; i < n; ++i) {
            int diff = target[i] - nums[i];
            if (diff == pre) {
                continue;
            }
            if ((diff > 0) == (pre > 0)) {
                if (abs(diff) > abs(pre)) {
                    res += abs(diff) - abs(pre);
                }
            } else {
                res += abs(diff);
            }
            pre = diff;
        }

        return res;
    }
};
