class Solution {
public:
    vector<int> diff;
    int calsign(int s){
        if (s>0) return 1;
        else if (s==0) return 0;
        else return -1;
    }
    long long res=0;
    int n;
    long long cal(int l,int r){
        long long sum=abs(diff[l]);
        for (int i=l+1;i<=r;i++){
            if (diff[i]==0) continue;
            else if(diff[i]>0){
                if (diff[i]<diff[i-1]) continue;
                else sum+=(diff[i]-diff[i-1]);
            } else{
                if (abs(diff[i])<abs(diff[i-1])) continue;
                else sum+=(abs(diff[i])-abs(diff[i-1]));
            }
        }
        return sum;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        res=0;
        int l=0;
        int r=0;
        n=nums.size();

        for (int i=0;i<n;i++) diff.emplace_back(target[i]-nums[i]);
        int sign = calsign(diff[0]);
        for (int i=1;i<n;i++){
            if (calsign(diff[i])!=sign){
                res+=cal(l,r);
                l = r = i;
                sign = calsign(diff[i]);
            } else{
                r++;
            }
        }
        res+=cal(l,r);
        return res;
    }
};
