class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        long long res=0;
        vector<long long> t(1);
        for(int i=0;i<n;++i){
            long long d=target[i]-nums[i];
            if(d>0){
                if(!t.empty()&&t.back()<0){
                    t=vector<long long>(1);
                }
                bool k=0;
                while(!t.empty()&&t.back()>d){
                    t.pop_back();
                    k=1;
                }
                if(!k)
                    res+=d-t.back();
                t.push_back(d);
            }
            else if(d<0){
                if(!t.empty()&&t.back()>0){
                    t=vector<long long>(1);
                }
                bool k=0;
                while(!t.empty()&&t.back()<d){
                    t.pop_back();
                    k=1;
                }
                if(!k)
                    res+=t.back()-d;
                t.push_back(d);
            }
            else{
                t=vector<long long>(1);
            }
            // cout<<"i="<<i<<" res="<<res<<endl;
        }
        return res;
    }
};
