class Solution {
public:
    long long solve(vector<int> &v){
        int n=v.size();
        long long ans=v[0];
        for(int i=1;i<n;i++){
            ans+=max(0,v[i]-v[i-1]);
        }
        return ans;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<int> a(n),b(n);
        for(int i=0;i<n;i++){
            if(nums[i]>target[i]){
                b[i]=nums[i]-target[i];
            }
            else if(nums[i]<target[i]){
                a[i]=target[i]-nums[i];
            }
        }
        // for(auto x:a){
        //     cout<<x<<" ";
        // }
        // cout<<endl;
        // for(auto x:b){
        //     cout<<x<<" ";
        // }
        // cout<<endl;
        return solve(a)+solve(b);
    }
};
