class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0,temp = 0;
        int n = nums.size();
        long long  last = 0;
        for (int i=0;i<n;i++)
        {
            temp = target[i]-nums[i];
            if(temp==0)
            {

            }
            else if(last>0 && temp>0)
            {
                if(last<temp)
                {
                    ans+=temp-last;

                }
            }
            else if(last<0 && temp<0)
            {
                if(abs(last)<abs(temp))
                {
                    ans+=abs(temp)-abs(last);
                }
            }
            else{
                ans+=abs(temp);
            }
            last  = temp;
        }
        return ans;
    }
};
