class Solution:
    def sol(self, cur_l):
        cur_min = min(cur_l)
        # print("cur_min", cur_min)
        self.ans += cur_min

        st = None
        for i in range(len(cur_l)):
            cur_l[i] -= cur_min
            # print("cur_l[i]", cur_l[i])
            if st is None:
                if cur_l[i] > 0:
                    st = i
                continue
            else:
                if cur_l[i] == 0:
                    self.sol(cur_l[st:i])
                    st = None

        if st is not None:
            self.sol(cur_l[st:])


    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        self.ans = 0
        _l = []
        for i in range(len(nums)):
            delta = target[i] - nums[i]
            if len(_l) == 0:
                if delta == 0:
                    continue
                is_neg = -1 if delta < 0 else 1
                _l.append(delta * is_neg)
            else:
                cur_sign = -1 if delta < 0 else 1
                if delta == 0:
                    self.sol(_l)
                    _l = []
                    continue

                if cur_sign != is_neg:
                    self.sol(_l)
                    _l = []
                    is_neg = -1 if delta < 0 else 1

                _l.append(delta * is_neg)

        if len(_l) != 0:
            self.sol(_l)

        return self.ans
