class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        N = len(nums)
        delta = [x - y for x, y in zip(nums, target)]
        #print(delta)

        total = 0
        current = 0
        for i in range(N):
            if current != delta[i]:
                if current < 0 and delta[i] < 0:
                    if current > delta[i]:
                        total += abs(delta[i] - current)
                        current = delta[i]
                    else:
                        current = delta[i]
                elif current > 0 and delta[i] > 0:
                    if current < delta[i]:
                        total += abs(delta[i] - current)
                        current = delta[i]
                    else:
                        current = delta[i]
                elif current < 0 and delta[i] > 0:
                    total += abs(delta[i])
                    current = delta[i]
                elif current > 0 and delta[i] < 0:
                    total += abs(delta[i])
                    current = delta[i]
                elif delta[i] == 0:
                    current = delta[i]
                else:
                    total += abs(delta[i])
                    current = delta[i]
        #print(delta)
        return total
