#define pb push_back
using ll = long long;
class Solution {
public:
    ll calc(vector<ll> nums){
        int n = nums.size();
        vector<pair<ll, int>> ord;
        for(int i = 0; i < n; i++){
            ord.pb({abs(nums[i]), i});
        }
        sort(ord.begin(), ord.end());
        vector<bool> active(n);
        ll nactive = 0;
        ll ans = 0;
        for(int i = n - 1; i >= 0; i--){
            ll val = ord[i].first; int idx = ord[i].second;
            int d = 1;
            for(int j = max(0, idx - 1); j <= min(n - 1, idx + 1); j++){
                d -= active[j];
            }
            active[idx] = 1;
            if(i < n - 1){
                ans += (nactive * (ord[i + 1].first - val));
                // cout << i << " " << nactive << " " << ans << " " << (ord[i + 1].first - val) << endl;
            }
            nactive += d;
        }
        ans += (nactive * (ord[0].first));
        return ans;
    }
    ll minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<ll> d(n);
        for(int i = 0; i < n; i++){
            d[i] = target[i] - nums[i];
        }
        vector<ll> curr;
        curr.pb(d[0]);
        ll ans = 0;
        for(int i = 1; i < n; i++){
            if(d[i - 1] * d[i] < 0){
                ans += calc(curr);
                curr.clear();
            }
            curr.pb(d[i]);
        }
        ans += calc(curr);
        return ans;
    }
};
