class Solution:
    def calculateMinimum(self, diff_array, greater_or_less_than):
        if not diff_array:
            return 0
        if len(diff_array) == 1:
            return diff_array[-1] if diff_array[-1] >= 0 else abs(diff_array[-1])
        if greater_or_less_than == True:
            ans = min(diff_array)
            diff_array = [diff_array[i] - ans for i in range(len(diff_array))]
        else:
            ans = abs(max(diff_array))
            increment = max(diff_array)
            diff_array = [diff_array[i] - increment for i in range(len(diff_array))]
        last_index = None
        for i in range(len(diff_array)):
            if diff_array[i] == 0:
                if last_index != None:
                    ans += self.calculateMinimum(diff_array[last_index: i], greater_or_less_than)
                    last_index = None
            elif last_index == None:
                last_index = i
        if last_index != None:
            ans += self.calculateMinimum(diff_array[last_index: len(diff_array)], greater_or_less_than)
        return ans



    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        # find subarrays that are all less or all more in a row -> subproblems
        # split these subproblems into more subproblems
        diff_array = [target[i] - nums[i] for i in range(len(target))]
        ans = 0
        greater = None # true = target is greater than nums
        last_index = None
        for i in range(len(diff_array)):
            if diff_array[i] == 0:
                if last_index != None:
                    ans += self.calculateMinimum(diff_array[last_index: i], greater)
                    last_index = None
                    greater = None
            elif greater == None:
                last_index = i
                greater = True if diff_array[i] > 0 else False
            elif (diff_array[i] < 0 and greater == True) or (diff_array[i] > 0 and greater == False):
                ans += self.calculateMinimum(diff_array[last_index: i], greater)
                last_index = i
                greater = True if diff_array[i] > 0 else False
        if last_index != None:
            ans += self.calculateMinimum(diff_array[last_index: len(diff_array)], greater)
        return ans
