class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        int n = a.size();
        long long na = 0, po = 0;
        int x = b[0] - a[0];
        if (x > 0) po += x;
        else na -= x;
        for (int i = 1; i < n; i++) {
            int d = (b[i] - b[i - 1]) - (a[i] - a[i - 1]);
            if (d > 0) po += d;
            else na -= d;
        }
        return max(po, na);
    }
};
