class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        def minNumberOperations(v: List[int]) -> int:
            n = len(v)
            dp = [0] * n
            decrement = False
            if v[0] < 0:
                dp[0] = -v[0]
                decrement = True
            else:
                dp[0] = v[0]
            for i in range(1, n):
                #print(dp, decrement, v[i])
                if not decrement:
                    if v[i] < 0:
                        dp[i] = dp[i-1] + abs(v[i])
                        decrement = True
                        continue
                    if v[i] <= v[i-1]:
                        dp[i] = dp[i-1]
                    else:
                        dp[i] = dp[i-1] + v[i] - v[i-1]
                else:
                    if v[i] > 0 :
                        dp[i] = dp[i-1] + abs(v[i])
                        decrement = False
                        continue
                    if v[i] >= v[i-1]:
                        dp[i] = dp[i-1]
                    else:
                        dp[i] = dp[i-1] + abs(v[i] - v[i-1])
            return dp[n-1]
        diff = [0] * len(nums)
        for i in range(len(nums)):
            diff[i] = target[i] - nums[i]
        print(diff)
        return minNumberOperations(diff)
