func minimumOperations(a []int, b []int) int64 {
	n := len(a)

	d := make([]int, n)
	for i := 0; i < n; i++ {
		d[i] = b[i] - a[i]
	}
	cnt := int64(0)
	cnt += int64(abs(d[0]))
	for i := 1; i < n; i++ {
		if d[i]*d[i-1] < 0 {
			cnt += int64(abs(d[i]))
		} else {
			if abs(d[i]) > abs(d[i-1]) {
				cnt += int64(abs(d[i-1] - d[i]))
			}
		}
	}
	return cnt

}

func abs(a int) int {
	if a < 0 {
		return -a
	}
	return a
}
