class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        for(int i = 0; i < n; i ++)
            nums[i] = nums[i] - target[i];

        for(int i = n - 1; i >= 1; i --) nums[i] = nums[i] - nums[i - 1];

        long long ponum = 0, uposnum = 0;
        for(int i = 0; i < n; i ++)
            if(nums[i] > 0) ponum += nums[i];
            else uposnum += nums[i];

        return max(ponum, abs(uposnum));
    }
};
