class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<long long>diff;
        for(int i=0;i<nums.size();i++){
            diff.push_back(target[i]-nums[i]);
        }
        long long prev=0;
        long long ans=0;
        for(int i=0;i<diff.size();i++){
            if(diff[i]>=0&&prev>=0){
                if(prev<=diff[i])
                    ans+=diff[i]-prev;
                prev=diff[i];
            }
            else if(diff[i]>=0&&prev<0){
                ans+=diff[i];
                prev=diff[i];
            }
            else if(diff[i]<=0&&prev<=0){
                if(diff[i]<=prev)
                    ans+=abs(diff[i]-prev);
                prev=diff[i];
            }
            else{
                ans+=abs(diff[i]);
                prev=diff[i];
            }
        }
        return ans;
    }
};
