class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> diff(n);
        for(int i = 0; i < n; i++) {
            diff[i] = target[i] - nums[i];
        }
        int ind = 0;
        long long ans = 0;
        while(ind < n) {
            int curr = diff[ind++];
            if(curr >= 0) {
                while(ind < n && diff[ind] >= 0) {
                    curr += max(diff[ind] - diff[ind - 1], 0);
                    ind++;
                }
            }
            else {
                curr = -curr;
                while(ind < n && diff[ind] < 0) {
                    curr += max(diff[ind - 1] - diff[ind], 0);
                    ind++;
                }
            }
            ans += curr;
        }
        return ans;
    }
};
