class Solution {
public:

long long minimumOperations(vector<int>& nums, vector<int>& target) {

    for(int i=0; i<nums.size(); ++i) nums[i]-=target[i];

    vector<int> diff(nums.size()+2,0);
    diff[0] = nums[0];
    for (int i = 1; i < nums.size(); i++) 
        diff[i] = nums[i] - nums[i - 1]; 
    diff.back() = -nums.back();

    sort(begin(diff),end(diff));

    long long ans = 0;

    for(int i=0, j=diff.size()-1; i<j; ) {

        if (diff[i]==0) { ++i; continue;}
        if (diff[j]==0) { --j; continue;}

        if (abs(diff[i]) > abs(diff[j])) {

            int val = abs(diff[j]);
            ans += val;

            diff[i] += val;
            diff[j] -= val;

        } else {

            int val = abs(diff[i]);
            ans += val;

            diff[i] += val;
            diff[j] -= val;

        }

    }

    return ans;
}
};
