#define ll long long

class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        ll n = nums.size();

        // vector<ll>mini(n,INT_MAX);
        for(ll i=0 ; i<n ; i++)
        {
            nums[i]-=target[i];
            // mini[i] = min(mini[i],nums[i]);
        }
        // ll last = 0;

        ll i=0;
        ll ops = 0;
        ll f = -1;
        for(ll ind = 0 ; ind<n ; ind++)
        {
            if(nums[ind]>=0)
            {
                if(f==1)
                {
                    ops += sol(i,ind-1,nums);
                    i=ind;
                }
                f=0;
            }

            else
            {
                if(f==0)
                {
                    ops+= sol(i,ind-1,nums);
                    i=ind;
                }
                f=1;
            }
        }

        ops += sol(i,n-1,nums);

        return ops;

    }

    ll sol(ll i, ll j, vector<int>&nums)
    {
        vector<ll>t;

        for(ll k=i ; k<=j ; k++)
        {
            t.push_back(abs(nums[k]));   
        }

        ll n = t.size();
        ll ops = t[0];

        for(ll k=1 ; k<n ; k++)
        {
            if(t[k]>t[k-1])
            {
                ops += (t[k]-t[k-1]);
            }
        }

        return ops;
    }


};
