class Solution:
    def numberOfSubstrings(self, s: str) -> int:
        idx0 = []
        n = len(s)
        idx0.append(-1)
        for i in range(n):
            if s[i] != '0':
                continue
            idx0.append(i)
        idx0.append(n)

        res = 0

        for i in range(len(idx0) - 1):
            cnt = idx0[i + 1] - idx0[i] - 1
            cur = (cnt + 1) * cnt
            cur //= 2
            res += cur

        for cnt in range(1, 200 + 1):
            for i in range(1, len(idx0) - cnt):
                j = i + cnt - 1
                minLen = cnt + cnt * cnt 
                for x in range(idx0[i], idx0[i-1], -1):
                    rmost = x + minLen - 1
                    if rmost >= idx0[j + 1]:
                        continue
                    res += idx0[j + 1] - max(rmost, idx0[j])

        return res
