class Solution {
    public int numberOfSubstrings(String s) {
        int n = s.length();
        int m = (int) Math.sqrt(n) + 1;
        int[][] a = new int[n][m];
        int ans = 0;
        if (s.charAt(0) == '0')
            a[0][1] = 1;
        else {
            a[0][0] = 1;
            ans++;
        }
        for (int i=1; i<n; i++) {
            int x = s.charAt(i) - '0';
            if (x == 1) {
                a[i][0] = 1;
                int t = 0;
                for (int j=0; j<m; j++) {
                    a[i][j] += a[i-1][j];
                    t += a[i][j];
                    ans += j == 0 ? t : Math.min(a[i][j], Math.max(0, t - j - j * j + 1));
                }
            } else {
                int t = 0;
                a[i][1] = 1;
                for (int j=1; j<m; j++) {
                    a[i][j] += a[i-1][j-1];
                    t += a[i][j];
                    ans += Math.min(a[i][j], Math.max(0, t - j - j * j + 1));
                }
            }
        }
        return ans;
    }
}
