class Solution {
public:
    int numberOfSubstrings(string s) {
        int ans = 0, n = s.size();
        vector<int> a;
        int t = 0;
        for(int i=0;i<n;i++){
            if(s[i] == '1')
                t++;
            else{
                a.push_back(t);
                t = 0;
            }
        }
        a.push_back(t);
        vector<int> pre(a.size() + 1, 0);
        for(int i=0;i<a.size();i++){
            pre[i + 1] = pre[i] + a[i];
        }
        for(int zero=0;zero<=200;zero++){
            for(int i=0;i<a.size();i++){
                int j = i + zero;
                if(j == i) {
                    ans += a[i] * (a[i] + 1) / 2;
                    continue;
                }
                if(j >= a.size())
                    break;
                int ex = pre[j + 1] - pre[i] - zero * zero;
                for(int t = 0;t <= a[i];t++){
                    ans += max({0, min(ex - t + 1, a[j] + 1)});
                }
                // cout << zero << ' ' << i << " " << ex << " " << a[j] << endl;
            }
        }
        return ans;
    }
};
