#include <bits/stdc++.h>

using namespace std;

class Solution {
public:
    int numberOfSubstrings(string s) {
        int ans = 0;

        int n = size(s);
        vector<int> zero = {-1};
        for (int i = 0; i < n; i++) {
            if (s[i] == '0') {
                zero.push_back(i);
            }
        }
        zero.push_back(n);

        for (int i = 1; i < size(zero); i++) {
            int x = zero[i] - zero[i-1] - 1;
            ans += x*(x+1)/2;
        }

        for (int i = 1; i <= 200; i++) {
            for (int j = 1; j+i-1 < size(zero)-1; j++) {
                int a = zero[j] - zero[j-1] - 1;
                int b = zero[j+i] - zero[j+i-1] - 1;
                int c = i*i - (zero[j+i-1] - zero[j] + 1 - i);

                int l = max(0, c-a), r = b;
                if (l > r) continue;
                int sa = min(a+1, a-(c-l)+1);

                int move = min(r-l, a+1-sa);
                ans += (sa + sa+move) * (move+1) / 2;

                if (move < r-l) {
                    ans += (r-l-move) * (a+1);
                }
            }
        }

        return ans;
    }
};
