class Solution:
    def numberOfSubstrings(self, s: str) -> int:
        zero_pos = []
        n = len(s)
        for i, c in enumerate(s):
            if c == '0':
                zero_pos.append(i)
        zero_pos = [-1] + zero_pos + [n]
        mx_zeros = round(n ** 0.5) + 2
        m = len(zero_pos)

        ans = 0
        for i in range(1, m):
            cnt_1 = zero_pos[i] - zero_pos[i - 1] - 1
            ans += cnt_1 * (cnt_1 + 1) // 2
        next_zero_pos_i = 0
        # print(ans)
        # print(zero_pos)
        for i in range(n):
            while zero_pos[next_zero_pos_i] < i:
                next_zero_pos_i += 1
            for zeros in range(1, mx_zeros):
                next_zero_pos_j = next_zero_pos_i + zeros - 1
                if next_zero_pos_j >= m - 1:
                    break
                r = zero_pos[next_zero_pos_j + 1] - 1
                l = max(zero_pos[next_zero_pos_j], i + zeros ** 2 + zeros - 1)
                if l >= n:
                    break
                # print(i, zeros, l, r)
                ans += max(r - l + 1, 0)
        return ans
