class Solution {
public:
    int numberOfSubstrings(string s) {
        int ans = 0;
        vector<int> zeroes = {-1};
        for (int i = 0; i < s.size(); ++i){
            if (s[i] == '0') zeroes.push_back(i);
            int totalOnes = i - zeroes.back();
            ans += totalOnes;
            int curZeroes = 0;
            for (int y = zeroes.size() - 1; y > 0; --y){
                ++curZeroes;
                if (curZeroes * curZeroes > i + 1) break;
                int newOnes = zeroes[y] - zeroes[y - 1] - 1;
                int requiredOnes = curZeroes * curZeroes;
                if (totalOnes >= requiredOnes) ans += newOnes + 1;
                else ans += max(0, totalOnes + newOnes - requiredOnes + 1);
                totalOnes += newOnes;
            }
        }
        return ans;
    }
};
