class Solution {
public:
    int numberOfSubstrings(string s) {
        vector<int> z;
        vector<int> pref(s.size());
        z.push_back(-1);
        int n = s.size();
        for (int i = 0; i < n; i++) {
            if (i) pref[i]=pref[i-1];

            if (s[i] == '0') z.push_back(i);
            else pref[i]++;
        }
        z.push_back(n);
        int ans = 0;

        for (int i = 0; i < z.size() - 1; i++) {
            ans += (z[i + 1] - z[i] - 1) * (z[i + 1] - z[i]) / 2;
        }
        // cout << ans << endl;
        for (int cnt = 1; cnt * cnt <= n; cnt += 1) {
            for (int l = 1; l + cnt < z.size(); l++) {
                int aft = z[l + cnt] - z[l + cnt - 1]-1;
                int bef = z[l] - z[l-1]-1;
                int need = cnt * cnt - pref[z[l + cnt - 1]] + pref[z[l]];
                if (aft > bef) swap(aft, bef);
                // cout << cnt << an
                // cout << bef << aft << need << endl;
                // ans += max((aft - need + 1) * bef , 0);
                // ans += max(sm(min(need, aft, 0)))
                for (int i = 0; i <= aft; i++) {
                    ans += max(bef - max(need - i, 0) + 1, 0);
                }
                // cout << ans << endl;
            }
        }

        return ans;
    }
};
