class Solution {
    public int numberOfSubstrings(String s) {
        List<Integer> index = new ArrayList<>();
        index.add(-1);
        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i) != '0') {
                continue;
            }
            index.add(i);
        }
        index.add(s.length());
        long result = 0;
        for (int i = 0; i < index.size() - 1; i++) {
            long count = index.get(i + 1) - index.get(i) - 1;
            result += ((count + 1) * count) / 2;
        }
        for (int count = 1; count <= 200; count++) {
            for (int i = 1; i < index.size() - count; i++) {
                int j = i + count - 1;
                int minSize = count + count * count;
                for (int x = index.get(i); x > index.get(i - 1); x--) {
                    int r = x + minSize - 1;
                    if (r >= index.get(j + 1)) {
                        continue;
                    }
                    result += index.get(j + 1) - Math.max(r, index.get(j));
                }
            }
        }
        return (int) result;
    }
}
