class Solution {
public:
    int numberOfSubstrings(string s) {
        int ans=0;
        int n=s.size();
        vector<int> p(n),suf(n,n);
        for(int i=n-2;i>=0;i--){
            if(s[i]=='0')suf[i]=i;
            else suf[i]=suf[i+1];
        }
        for(int i=0;i<201;i++){
            int cnt=0;
            int ind=0;
            for(int j=0;j<n;j++){
                if(s[j]=='0')cnt++;
                if(cnt<i)continue;
                while(cnt>i){
                    cnt-=(s[ind++]=='0');
                }

                // ind , j 
                if(j-ind+1-i>=i*i && j>=ind){
                    int ex=(j-ind+1-i)-i*i;
                    ans+=min({suf[ind],j,ind+ex})-ind+1;
                    // cout<<j<<" "<<ind<<" "<<suf[ind]<<" "<<min(suf[ind],j)-ind+1<<endl;;
                }
            }
        }
        return ans;
    }
};
