import numpy as np
class Solution:
    def numberOfSubstrings(self, s: str) -> int:
        ones = []
        c = 0
        for n in s:
            if n == '0':
                ones.append(c)
                c = 0
            else:
                c += 1
        ones.append(c)
        print(ones)
        n = len(s)
        cusum = np.cumsum([0] + ones)
        res = 0
        for i in ones:
            res += i*(i+1) / 2
        for i in range(1,int(np.sqrt(cusum[-1])) + 1):
            for j in range(len(cusum)-i-1):
                nones = cusum[j+i+1] - cusum[j]
                if nones >= i**2:
                    l = ones[j]
                    r = ones[j+i]
                    slack = nones - i**2
                    for l1 in range(l+1):
                        if l1 > slack:
                            break
                        res += min(r,slack - l1) + 1
        return int(res)
