class Solution:
    def numberOfSubstrings(self, s: str) -> int:
        l=len(s)
        sm=0
        dct=defaultdict(list)
        for i, c in enumerate(s):
            dct[sm].append(i)
            sm+=c=='0'
        dct[sm].append(l)

        ll=int(sqrt(l))
        print(dct)
        ans=0
        for lst in dct.values():
            l2=len(lst)
            ans+=l2*(l2-1)//2
        for k in range(1,ll+1):
            kk=k+k*k
            for key in dct:
                if key+k in dct:
                    lst1=dct[key]
                    lst2=dct[key+k]
                    l2=len(lst2)
                    for i in lst1:
                        ans+=l2-bisect.bisect_left(lst2, i+kk)
        return ans
