class Solution {
public:
    int numberOfSubstrings(string s) {
        vector<int> zeroPos;
        zeroPos.push_back(-1);
        int ans=0;
        int n=s.size();
        for(int i=0;i<n;i++){
            int zc=0,r=i;
            if(s[i]=='0'){
                zeroPos.push_back(i);
            }
            else {
                ans+=i-zeroPos.back();
                r=zeroPos.back();
            }
            for(int j=zeroPos.size()-1;j>0;j--){
                zc++;
                int pos=i+1-zc-zc*zc;
                if(pos<0)break;
                if(pos<=zeroPos[j-1])continue;
                r=min(pos,r);
                ans+=r-zeroPos[j-1];
                r=zeroPos[j-1];
            }
        }
        return ans;
    }
};
