class Solution {
public:
    int numberOfSubstrings(string s) {
        s = "0" + s;
        vector<int> p;

        int ans = 0;
        for (int i = 0; i < s.size(); i++) {
            if (s[i] == '0') p.push_back(i);
            int l = i + (s[i] == '1');
            int tot = 0;
            for (int j = (int)p.size() - 1, c = 0; j >= 0; j--, c++) {
                if(p.size() - j > 200) break;
                int one = i - p[j] - c;
                int val = 0;
                val += max(0, min(one - c * c + 1, l - p[j] - 1));
                if (j > 0 && (c + 1) * (c + 1) <= one) val++;
                // cout << p[j] << " " << i << " " << val << endl;
                tot += val;
                l = p[j];
            }
            // cout << i << " " << tot << endl << endl;
            ans += tot;
        }

        return ans;
    }
};
