class Solution {
public:
    int numberOfSubstrings(string s) {
        vector <int> a; a.push_back(-1);
        int n = s.size();
        int ret = 0;
        for (int i = 0; i < n; i++) {
            if (s[i] == '0')
                a.push_back(i);
            int j = a.size() - 1;
            int k = i, cnt = 0;
            while (k >= 0) {
                int d = k - a[j];
                int infl = cnt * cnt + cnt - 1;
                int id = i + 1 - infl;
                // cout << "k: " << k << " " << id << " " << d << endl;
                if (id < 0) break ;
                if (k < id) {
                    ret += d;
                }
                else {
                    if (a[j] < id)
                        ret += id - a[j] - 1;
                }
                cnt++;
                k = a[j];
                j--;
            }
            // cout << ret << " " << i << endl;
        }
        return ret;
    }
};
