#include "ext/pb_ds/assoc_container.hpp"
#include "ext/pb_ds/tree_policy.hpp"
using namespace __gnu_pbds;
template<typename T,class C=std::less_equal<T>>
using ordered_set=tree<T,null_type,C,rb_tree_tag,tree_order_statistics_node_update>; 

class Solution {
public:
    int numberOfSubstrings(string s) 
    {
        int ans=0;
        vector<ordered_set<int>>oset_map(s.size()+1);
        oset_map[0].insert(0);
        int max0=sqrt(s.size()+1);
        for(int a=0,b=0,i=0;i<s.size();i++)
        {
            if(s[i]=='1')
            {
                a++;
            }
            else
            {
                b++;
            }
            for(int y=b;y>=0&&y>=b-max0;y--)
            {
                ans+=oset_map[y].order_of_key(a-(y-b)*(y-b)+1);
            }
            oset_map[b].insert(a);
        }
        return ans;    
    }
};
