from math import ceil, sqrt


class Solution:
    def numberOfSubstrings(self, s: str) -> int:
        n=len(s)
        sq=ceil(sqrt(n))+1
        zs=[]
        res=0
        for i in range(n):
            if s[i]=='0':
                zs.append(i)
            if len(zs)>sq and zs!=[]:
                zs.pop(0)
            left=i
            cnt=0
            acc=0
            for j in zs[::-1]:
                cnt+=1
                nleft=max(-1,i-(cnt**2+cnt)+1)
                if nleft<j:
                    acc+=min(j,left)-nleft
                left=nleft
            res+=i+1-acc
        return res
