class Solution {
public:
    int numberOfSubstrings(string s) {
        int len = s.size();
        vector<int> one(len + 1);
        for(int i = 0; i < len; i++){
            one[i + 1] = one[i] + (s[i] == '1'? 1: 0);
        }
        int ans = 0;
        for(int i = 0; i < len; i++) {
            int j = i;
            while(j < len) {
                int ocnt = one[j + 1] - one[i];
                int zcnt = j - i + 1 - ocnt;
                if(ocnt >= zcnt*zcnt) {
                    int n = 0;
                    n = min(len - j, (int)sqrt(ocnt) - zcnt);
                    n = max(n, 1);
                    ans += n;
                    j += n;
                }else {
                    j += zcnt*zcnt - ocnt;
                }
            }
        }
        return ans;
    }
};
