class Solution {
public:
    int numberOfSubstrings(string s) {
        vector<int> qs(s.length() + 1);
        int n = s.length();
        s = '#' + s;
        int ans = 0;
        vector<int> pos(1);
        for (int i = 1; i <= n; ++i) {
            qs[i] = qs[i -1] + (s[i] == '1');
            if (s[i] == '0') {
                pos.push_back(i);
            }
            int cur = i, sz = pos.size(); 
            for (int j = 0; j < min(sz, 201); ++j) {
                int r = pos[sz -j -1] + 1;
                if (r > cur) {
                    cur = pos[sz -j -1];
                    continue;
                }
                int mi = qs[i] - qs[cur -1], ma = qs[i] - qs[r -1];
                if (ma >= j * j) {
                    ans += ma - max(j * j, mi) + 1;
                    // cout << i << " " << j << ma - max(j * j, mi) +1 << "\n";
                }
                cur = pos[sz -j -1];
            }
        }
        return ans;
    }
};
