class Solution {
public:

    vector<int> zero, one;
    void pre(string s)
    {
        one.push_back(0);
        zero.push_back(1);
        for(auto c:s)
        {
            if(c == '1')
            {
                one.push_back(one.back() + 1);
                zero.push_back(zero.back());
            }
            else
            {
                zero.push_back(zero.back() + 1);
                one.push_back(one.back());
            }
        }
    }

    pair<int,int> find(int l, int r)
    {
        return {zero[r+1] - zero[l], one[r+1] - one[l]};
    }

    int numberOfSubstrings(string s) {
        int ans = 0;
        int n = s.length();
        pre(s);
        for(int st = 0; st < n; st++)
        {
            int end = st;
            while(end < n)
            {
                pair<int,int> p = find(st, end);
                int z = p.first;
                int o = p.second;
                if(o >= z*z)
                {
                    int cnt = floor(sqrt(o)) - z;
                    if(end + cnt + 1 >= n)
                    {
                        cnt = n-1-end;
                        ans+=cnt+1;
                        break;
                    }
                    else
                    {
                        ans += cnt+1;
                        end += cnt+1;
                    }
                }
                else
                {
                    int cnt = z*z - o;
                    end += cnt;
                }
            }
        }
        return ans;
    }
};
