class Solution {
public:
    int numberOfSubstrings(string s) {
        int n = s.length();
        int count = 0;
        bool v[n];
        memset(v, false, sizeof(v));
        for(int i = 0; i < n; i++)
            v[i] = s[i] - '0';
        vector<int> nxt(n);
        int last = n;
        for(int i = n - 1; i >= 0; i--)
        {
            if(v[i]) nxt[i] = last;
            else last = i;
        }
        for (int i = 0; i < n; ++i) 
        {
            int ones = 0, zeros = 0, j = i;
            if(v[i] == true)
                j = nxt[i];
            ones = j - i, zeros = 0;
            count += ones;

            for (; j < n; j++) 
            {
                if (v[j]) ones++;
                else zeros++;

                if(zeros * zeros > n) break;
                if (ones >= zeros * zeros)
                    ++count;
            }
        }
        return count;
    }
};
